import { Link } from 'react-router-dom'; //Link Component can be wrapped around the our link text, it will also render the anchor tag at the end, but internally react router dom attaches a click listener to the anchor tag, and when we click on it, it will prevent the browser default of sending a request, and instead just parse the url we wanted to go to, change it in the browser url bar, but not send a request, instead load the appropriate component on the screen just with react and js, so that we stay on the already loaded page and dont send an extra request

//create react app has a feature built in that allows us to scope styles to components, that is called css "modules"

import classes from './MainNavigation.module.css'; //classes is an object that has all the css classes defined in the css files as the properties of this object
import { useContext } from 'react';
import FavoritesContext from '../../store/favorites-context';

function MainNavigation() {
  const favoritesCtx = useContext(FavoritesContext);
  return (
    <header className={classes.header}>
      <div className={classes.logo}>React Contacts</div>
      <nav>
        <ul>
          <li>
            {/* <a href=""></a>  //we dont use this anchor tag, as it has a disadvantage, every time we click on the link, new request will be sent to the server, the server that is hosting the react application, and the server will reply to our application, and then the router will figure out which page to load, but we sent this request first, which is redundant, we already are in our running react application, and it would be great if we dont leave it just because we want to navigate somewhere, sending the extra request is not something we want to do */}
            <Link to="/">All Contacts</Link>
          </li>
          <li>
            <Link to="/new-contact">Add new Contact</Link>
          </li>
          <li>
            <Link to="/favorites">
              My Favorites
              <spam className={classes.badge}>
                {favoritesCtx.totalFavorites}
              </spam>
            </Link>
          </li>
        </ul>
      </nav>
    </header>
  );
}

export default MainNavigation;
