import { useRef } from 'react';
import Card from '../ui/Card';
import classes from './NewContactForm.module.css';

function NewContactForm(props) {
  //1st ref is for the title input
  const nameInputRef = useRef(); //we create a reference object by calling the "useRef()", we store this object returned

  const imageInputRef = useRef();
  const numberInputRef = useRef();
  const addressInputRef = useRef();

  function submitHandler(event) {
    // the default behaviour of the browser is to sent a request to the server serving this page to automatically, when submitted
    event.preventDefault(); //used to prevent the browser default behaviour of reloading the page when the form is submitted
    //we want to send the http request behind the scene without reloading the page

    //we want to read the values that were given by the users

    //we can either use "useState()" again and at the "onChange" event listener that we can put in the input tag, but this way, at every keystroke, the function is triggered as the state is changing at every ketstroke, and then we can extract the value from the event object which we will recieve for that event and update our state for that given input with the entered value

    //or we can use "ref"
    //"ref" stands for reference, and react allows us to set up references to DOM elements, we can get direct access to DOM elements

    const enteredName = nameInputRef.current.value;
    // titleInputRef.current.value = 'Some new value';  //we can also change it, but if we want to change what is shown on the screen we should  use "useState"

    //"useRef" is good for reading input values

    const enteredImage = imageInputRef.current.value;
    const enteredNumber = numberInputRef.current.value;
    const enteredAddress = addressInputRef.current.value;

    const contactData = {
      name: enteredName,
      image: enteredImage,
      number: enteredNumber,
      address: enteredAddress,
    };

    // console.log(meetupData);
    //we want to sent meetup data to the server, with react (SPA applications) we need a backend apis to send requests
    //we dont connect a frontend application to a database itself as it will have security issues, all the code that we write in the React application is exposed to the visitors of the page, and database credentials would be in there as well, thats why we need a backend api (backend server), to which we can send requests, and then its that backend server that connects to a database and stores data, we use firebase as dummy backend

    props.onAddMeetup(contactData);
  }

  return (
    <Card>
      <form className={classes.form} onSubmit={submitHandler}>
        {/* we can add onSubmit={submitHandler}, when we submit the form, the "submit" event be caught here and the function will be called */}
        <div className={classes.control}>
          <label htmlFor="title">Name</label>
          {/* "for" is a keyword in js, so we set this using htmlFor */}
          <input type="text" required id="name" ref={nameInputRef} />
          {/* another special prop is "ref" is used to connect the created ref object to the input */}
        </div>
        <div className={classes.control}>
          <label htmlFor="image">Person Image</label>
          <input type="url" required id="image" ref={imageInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor="number">Number</label>
          <input type="number" required id="number" ref={numberInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor="address">Address</label>
          <textarea
            id="address"
            required
            rows="5"
            ref={addressInputRef}
          ></textarea>
        </div>
        <div className={classes.actions}>
          <button>Add Contact</button>
          {/* by default a "subsmit" event will be emmited, when clicked on the button in the form, we can then catch the event in the form tag */}
        </div>
      </form>
    </Card>
  );
}

export default NewContactForm;
