import { useHistory } from 'react-router-dom'; //another hook

import NewContactForm from '../components/contacts/NewContactForm';

function NewContactPage() {
  const history = useHistory(); //gives us a history object, and that object exposes certain methods to us that allows us to  manipulate the browser history, (eg.-to navigate away)

  function addContactHandler(meetupData) {
    fetch(
      'https://react-meetup-e3cd5-default-rtdb.firebaseio.com/meetups.json',
      //used for sending http request, we can also use  3rd party packages"axios" for sending an http request

      //firebase requires us to add ".json" at the end of the link
      {
        method: 'POST', //define the http method, be default it is GET request
        body: JSON.stringify(meetupData), //data which we want to store, in the JSON format
        header: {
          'Content-Type': 'application/json',
        },
      }
    ).then(() => {
      history.replace('/'); //we want to navigate away from the form to the home page
    });
  }

  return (
    <section>
      <h1>Add New Contact</h1>
      <NewContactForm onAddContact={addContactHandler} />
    </section>
  );
}

export default NewContactPage;
